export function addHint(abi, name, fn) {
    for (let elm of abi) {
        if (elm.type !== 'function' && elm.type !== 'event')
            continue;
        if (elm.name === name)
            elm.hint = fn;
    }
}
export function addHints(abi, map) {
    Object.keys(map).forEach((name) => {
        addHint(abi, name, map[name]);
    });
}
export function addHook(abi, name, fn) {
    for (let elm of abi) {
        if (elm.type !== 'function')
            continue;
        if (elm.name === name)
            elm.hook = fn;
    }
}
